data.code:
// Ensure unique values only
=COUNTIF($A$1:$A$100,A1)=1

// Validate email format
=AND(ISNUMBER(FIND("@",A1)),ISNUMBER(FIND(".",A1)))

// Ensure positive numbers only
=A1>0

// Validate date is not in the past
=A1>=TODAY()

// Highlight cells above average
=A1>AVERAGE($A$1:$A$100)

// Color-code based on multiple conditions
=AND(A1>100,B1="High Priority")

// Highlight weekends
=WEEKDAY(A1,2)>5

// Show overdue items
=AND(A1<TODAY(),B1="Pending")

// Create dynamic named range
=OFFSET(Sheet1!$A$1,0,0,COUNTA(Sheet1!$A:$A),1)

// Use in chart data source
=Sheet1!DynamicRange

// Auto-update when data changes
// No manual chart updates needed
